        private void button2_Click(object sender, EventArgs e)
        {
            sidePanel.Height = button2.Height;
            sidePanel.Top = button2.Top;

            textBox1.Text = "Первым делом, чтобы начать устанавливать систему себе на компьютер, нам нужно убедиться в том, " +
                "что аппаратные возможности нашего ПК без проблем позволят нам работать с ОС linux без всяких «танцев с бубном»." +
                " В настоящее время у современных linux дистрибутивов(о дистрибутивах и чем они отличаются друг от друга мы поговорим позднее) " +
                "не возникают серьёзных или по крайней мере не решаемых проблем с железом, однако камнем преткновения может послужить видеокарта " +
                "от Nvidia(об этом тоже в следующих главах). В своей  структуре, ОС linux – довольно нетребовательные к железу, однако, проверить " +
                "то, на сколько хорошо ваш ПК «дружит» с linux можно воспользоваться следующими вариантами:\r\n1.Первым делом нам нужно узнать " +
                "системные требования для выбранной нами ОС, если всё в порядке, то выполняем действия ниже: заходим на официальный сайт Ubuntu:" +
